sp_widget.client_script:
function($rootScope, $scope, spModal) {
 var c = this;


	$scope.cancelReservation = function() {
	$scope.data.action='delete';

		spModal.confirm("Are you sure you want to cancel this reservation?").then(function(confirmed){
				 if(confirmed){
					c.server.update();

					 var url = '/csr?id=pending_approvals';
					top.location.href = url;
				 }
			 });



}
}

