sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	if(current.type == "business" && current.active == true){
		var table = new GlideRecord("x_616811_coworking_calendar");
		table.addQuery("type", "business");
		table.addQuery("active", true);
		table.query();

		if( !current.isNewRecord()){

			if(table.getRowCount() > 0){
				table.next();
// 				gs.addErrorMessage(table.getRowCount()+" | "+"asd");
// 				current.setAbortAction(true);
				if(table.u_number != current.u_number){
					gs.addErrorMessage("Only 1 active business day is allowed. Set active business record to"+
										" inactive first");
				current.setAbortAction(true);
				}

			}
		} else{
			if(table.getRowCount() > 0){
				gs.addErrorMessage("Only 1 active business day is allowed. Set active business record to inactive first");
				current.setAbortAction(true);
			}
		}

	}
})(current, previous);
