sys_script_client.script:
function onLoad() {


	if(g_form.isNewRecord()){

		g_form.removeOption('Days', '1');
		var script = new GlideAjax("ReservationClientUtils");
		script.addParam("sysparm_name", "getBusinessdays");
		script.getXML(getAnswer);
	}
	function getAnswer(response){
		var days = ["null","Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
		var scriptResponse = response.responseXML.documentElement.getAttribute("answer");

		var daysArray = scriptResponse.split(",");
		var businessDays = "";
		for(var i=0; i< daysArray.length;i++){
			if(i==0){
				businessDays =  days[daysArray[i]];
			} else{
				businessDays = businessDays +", "+ days[daysArray[i]];
			}
// 			g_form.addOption("days", daysArray[i], days[daysArray[i]]);
		}
		g_form.showFieldMsg("days","Business days: "+ businessDays, "info", true);
	}


}
