sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var checkIf = new GlideAjax('ClientScriptInclude');
    // Specify the getEmail method
    checkIf.addParam('sysparm_name','startIsPastCurrentTime');
    // Pass the Requested for sys_id
    checkIf.addParam('sysparm_start', newValue);
    // Send the request to the server
    checkIf.getXML(populateEmailField);
// g_form.getValue('start')

    // When the response is back from the server
    function populateEmailField(response){
        // Extract the email address from the response, clear any value from the email field, 
        // set new value in the email field
        var answer = response.responseXML.documentElement.getAttribute("answer");
		if(answer == "true"){
			alert("Start time must not be past the current time");
			g_form.clearValue('u_start_time');

		}
//         g_form.clearValue('u_requested_for_email');
//         g_form.setValue('u_requested_for_email',emailFromScriptInclude);
    }




}
