sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;

   }
	var script = new GlideAjax('VerifySpaceAccessibility');
	script.addParam('sysparm_name','isRoomOrOffice');
	script.addParam('sysparm_spaceID', newValue);
	script.getXML(populateEmailField);

	// When the response is back from the server
	function populateEmailField(response){
		// Extract the email address from the response, clear any value from the email field, 
		// set new value in the email field
		var isRoomOrOffice = response.responseXML.documentElement.getAttribute("answer");
		if(isRoomOrOffice  && !g_user.hasRole("x_616811_coworking.csr_patron")){
			alert("Sorry, only registered users can reserve this type of Space");
			g_form.clearValue("space");

		}
	}

}
