sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var table = new GlideRecord("sys_user_grmember");
	table.addQuery("group.name", "CSR Manager");
	table.query();
	while(table.next()){
		var userTable = new GlideRecord("sys_user");
		userTable.get(table.getValue("user"));
		userTable.query();
		userTable.next();

		email.addAddress("cc",userTable.getValue("email") ,userTable.getValue("name"));

	}




})(current, template, email, email_action, event);
