sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var included = "";
	var ctr = 0;
	var start;
	var end;
	if(current.duration_type == "hourly"){
		start = new GlideDateTime(current.u_start_time);
		end = new GlideDateTime(current.u_end_time);
	} else{
		start = new GlideDateTime(current.u_start_date);
		end = new GlideDateTime(current.u_end_date);
	}
	if(current.duration_type == "hourly"){

		var hour = new GlideTime();
		hour.setValue("01:00:00");

		while(start <= end){
			if(ctr == 0){
				included = start.getDate();
			} else if(start.getTime() < end.getTime()){
				included = included +" "+ start.getTime().getByFormat("HH");
			} 
// 			else{
// 				included = included + " " + start.getTime().getByFormat("HH");
// 			}

			start.add(hour);
			ctr = ctr +1;
		}
		current.datetime_included = included;

	} else if(current.duration_type == "daily"){

		while(start.getDate() <= end.getDate()){

			if(ctr == 0){
				var yearMonth = start.toString().split("-")[0] +"-"+start.toString().split("-")[1];
				included = yearMonth +" "+ start.getDayOfMonthUTC();
			} else if(start.getDate() < end.getDate()){
				included = included +" "+ start.getDayOfMonthUTC();
			} else{
				var yearMonth1 = start.toString().split("-")[0] +"-"+start.toString().split("-")[1];
				included = included + " " + start.getDayOfMonthUTC() +" "+ yearMonth1;
			}

			start.addDaysUTC(1);
			ctr = ctr +1;
		}

		current.date_included = included;
	}



})(current, previous);
