sys_script_include.script:
var ClientScriptInclude = Class.create();
ClientScriptInclude.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmail: function() {
        var userRecord = new GlideRecord("sys_user");
        userRecord.get(this.getParameter('sysparm_userID'));
        return userRecord.email + '';
    },

	startIsPastCurrentTime: function(){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var startTime = new GlideDateTime(this.getParameter('sysparm_start'));
		gs.info(startTime);
		// If the testdate is before rightnow, return true, else return false
		if (startTime.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},
    type: 'ClientScriptInclude'
});
