sys_script_include.script:
var ReservationUtils = Class.create();
ReservationUtils.prototype = {
    initialize: function() {
    },
	startIsPastCurrentTime: function(start){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var startTime = new GlideDateTime(start);

		// If the testdate is before rightnow, return true, else return false
		if (startTime.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	endIsBeforeStart: function(start, end){

		// Create GlideDateTime objects for the current date and the passed in date
		var startTime = new GlideDateTime(start);
		var endTime = new GlideDateTime(end);

		// Get the date portion of rightnow and testdate (no timestamp)
		if(endTime.before(startTime)){
			return true;
		}
		else{
			return false;

		}

		// Compare today and istoday to see if they are the same day
// 		if(today.compareTo(istoday) == 0){
// 			return true;
// 		}
// 		else {
// 			return false;
// 		}
	},

    type: 'ReservationUtils'
};
